class OW_Toolkit_Admin{	/**	 * The ID of this plugin.	 *	 * @since    1.0.0	 * @access   private	 * @var      string    $ow_toolkit    The ID of this plugin.	 */	private $ow_toolkit;	/**	 * The version of this plugin.	 *	 * @since    1.0.0	 * @access   private	 * @var      string    $version    The current version of this plugin.	 */	private $version;	/**	 * Initialize the class and set its properties.	 *	 * @since    1.0.0	 * @var      string    $ow_toolkit       The name of this plugin.	 * @var      string    $version    The version of this plugin.	 */	public function __construct( $ow_toolkit, $version )	{		$this->ow_toolkit = $ow_toolkit;		$this->version = $version;	}	/**	 * Register the stylesheets for the Dashboard.	 *	 * @since    1.0.0	 */	public function enqueue_styles() {		wp_enqueue_style( $this->ow_toolkit, plugin_dir_url( __FILE__ ) . 'css/owtoolkit_admin.css', array(), $this->version, 'all' );	}
	/**
	 * Register the JavaScript for the dashboard.
	 * 
	 * @since    1.0.0 	 */ 	public function enqueue_scripts() { 		wp_enqueue_script( $this->ow_toolkit, plugin_dir_url( __FILE__ ) . 'js/owtoolkit_admin.js', array( 'jquery' ), $this->version, false ); 	}	/**	 * Register Post Type	 *	 * Creates the post type. Some elements may be overwritten with hooks.	 *	 * @since    1.0.0	 */	/* Price Table CPT */	function plan_post_init()	{		$labels = array( 			'name' => _x( 'Plan', 'pricing', 'ulysses' ),			'singular_name' => _x( 'Plan', 'pricing', 'ulysses' ),			'add_new' => __( 'Add New', 'Plan', 'ulysses' ),			'add_new_item' => __( 'Add New Plan', 'pricing', 'ulysses' ),			'edit_item' => __( 'Edit Plan', 'pricing', 'ulysses' ),			'new_item' => __( 'New Plan', 'pricing', 'ulysses' ),			'view_item' => __( 'View Plan', 'pricing', 'ulysses' ),			'search_items' => __( 'Search Plan', 'pricing', 'ulysses' ), 			'not_found' => __( 'No Plan found', 'pricing', 'ulysses' ),			'not_found_in_trash' => __( 'No Plan found in Trash', 'pricing', 'ulysses' ),			'parent_item_colon' => __( 'Parent Plan', 'pricing', 'ulysses' ),			'menu_name' => __( 'Price Table', 'pricing', 'ulysses' ),		);		$args = array( 			'labels' => $labels,			'hierarchical' => true,			'supports' => array( 'title','page-attributes' ),			'rewrite'  => array( 'slug' => 'plan-item' ), /* you can specify its url slug */			'public' => true,			'show_ui' => true,			'show_in_menu' => true,			'show_in_nav_menus' => true,			'publicly_queryable' => true,			'exclude_from_search' => false,			'has_archive' => false,			'query_var' => true,			'can_export' => true,			'rewrite' => true,			'menu_position' => 101,			'menu_icon' => 'dashicons-schedule',			'capability_type' => 'post'		);		register_post_type( 'price_table', $args );	}	/* Slider CPT */	function slider_post_init()	{		$labels = array( 			'name' => _x( 'Slider', 'slider', 'ulysses' ),			'singular_name' => _x( 'Slider', 'slider', 'ulysses' ),			'add_new' => __( 'Add New', 'Slide', 'ulysses' ),			'add_new_item' => __( 'Add New Slide', 'slider', 'ulysses' ),			'edit_item' => __( 'Edit Slide', 'slider', 'ulysses' ),			'new_item' => __( 'New Slide', 'slider', 'ulysses' ),			'view_item' => __( 'View Slide', 'slider', 'ulysses' ),			'search_items' => __( 'Search Slide', 'slider', 'ulysses' ),			'not_found' => __( 'No Slide found', 'slider', 'ulysses' ),			'not_found_in_trash' => __( 'No Slide found in Trash', 'slider', 'ulysses' ),			'parent_item_colon' => __( 'Parent Slider', 'slider', 'ulysses' ),			'menu_name' => __( 'Slides', 'slider', 'ulysses' ),		);		$args = array( 			'labels' => $labels,			'hierarchical' => false,			'supports' => array( 'title' ),			'rewrite'  => array( 'slug' => 'slide-item' ), /* you can specify its url slug */			'public' => false,			'show_ui' => true,			'show_in_menu' => true,			'show_in_nav_menus' => true,			'publicly_queryable' => true,			'exclude_from_search' => false,			'menu_icon' => 'dashicons-format-image',			'has_archive' => false,			'query_var' => true,			'can_export' => true,			'rewrite' => true,			'menu_position' => 101,			'capability_type' => 'post'		);		register_post_type( 'slider', $args );	}
