<?php function ulysses_aboutus( $atts ){	global $ulysses_option;    extract(shortcode_atts(array(		'section_id' => '',		'title' => '',		'sub_title' => ''    ), $atts));	if( '' === $section_id ) :		$section_id = __('aboutus','ulysses');	endif;	// START ABOUT US SECTION 	ob_start();	?>	<div class="about-section" id="<?php echo esc_attr( $section_id ); ?>">		<div class="container">			<div class="site-title wow fadeInDown">				<h1><?php echo esc_attr( $title ); ?></h1>				<p><?php echo esc_attr( $sub_title ); ?></p>				<div class="site-dots d-text-c"><i class="fa fa-times-2"></i><i class="fa fa-times-2"></i></div>			</div>			<div class="row">				<div class="col-md-6">					<div class="img-box">						<img src="<?php echo esc_url( $ulysses_option['opt_about_left_img']['url'] ); ?>" alt="presentation" />					</div>				</div>				<div class="col-md-6">					<div class="services-mark-1">						<?php						if(!empty($ulysses_option['opt_about_content'])):							$about_contents = $ulysses_option['opt_about_content'];							foreach ( $about_contents as $about_content ) :								$about_icon_url = $about_content['attachment_id'];								$about_icon = wp_get_attachment_image ( $about_icon_url , 'thumb-120-120' );								?>								<div class="one-service">									<?php echo $about_icon; ?>									<h4><a href="<?php echo esc_url($about_content['url']); ?>"><?php echo esc_attr( $about_content['title'] ); ?></a></h4>									<p><?php echo $about_content['description']; ?></p>								</div>								<?php							endforeach;						endif;						?>					</div>				</div>			</div>		</div>	</div>	<?php	return ob_get_clean();}add_shortcode('ulysses_aboutus', 'ulysses_aboutus');?>