<?php function ulysses_contact( $atts ){	global $ulysses_option;    extract(shortcode_atts(array(		'section_id' => '',		'title' => '',		'sub_title' => ''    ), $atts));   if( '' === $section_id ) :        $section_id = __('contact','ulysses');   endif;		// START CONTACT SECTION 	ob_start();	?>	<div id="<?php echo esc_attr( $section_id ); ?>" class="contact-section">		<div class="container">			<div class="site-title">				<h1><?php echo esc_attr( $title ); ?></h1>				<p><?php echo esc_attr( $sub_title ); ?></p>				<div class="site-dots d-text-c"><i class="fa fa-times-2"></i><i class="fa fa-times-2"></i></div>			</div>			<div id="contact_form">				<div class="contact-form d-bg-c">					<?php					if(isset($ulysses_option['opt_contact_iframe'])) :						echo do_shortcode($ulysses_option['opt_contact_iframe']);					endif;					?>				</div>			</div>		</div>		<div class="map-location">			<div class="gmap" data-address="<?php echo esc_attr( $ulysses_option['opt_contact_address'] ); ?>" data-marker="<?php echo esc_url( THEME_URI ).'/images/marker.png'; ?>" data-markertxt="<?php echo esc_attr($ulysses_option['opt_contact_address']); ?> Email: <?php echo esc_attr($ulysses_option['opt_contact_email']); ?>">				<div id="map_addresses" class="map"></div>			</div>		</div>	</div>	<?php	return ob_get_clean();}add_shortcode('ulysses_contact', 'ulysses_contact');?>