<?php function ulysses_pricetable( $atts ){	global $ulysses_option;    extract(shortcode_atts(array(		'section_id' => '',		'title' => '',		'sub_title' => ''    ), $atts));	if( '' === $section_id ) :		$section_id = __('pricetable','ulysses');	endif;	// START PRICING TABLES SECTION 	ob_start();	?>	<div class="pricing-section" id="<?php echo esc_attr( $section_id ); ?>">		<div class="container">			<div class="site-title wow fadeInDown">				<h1><?php echo esc_attr( $title ); ?></h1>				<p><?php echo esc_attr( $sub_title ); ?></p>				<div class="site-dots d-text-c"><i class="fa fa-times-2"></i><i class="fa fa-times-2"></i></div>			</div>			<div class="row">				<?php				$args = array(					'post_type' => 'price_table',					'posts_per_page' => 4,					'orderby' => 'menu_order',					'order' => 'ASC',				);				$my_query = new WP_Query($args);				while ($my_query->have_posts()) :					$my_query->the_post();					$plan_features = get_post_meta( get_the_ID(), 'plan_features', false );					$plan_duration = get_post_meta( get_the_ID(), 'plan_duration', true );					$plan_active = get_post_meta( get_the_ID(), 'plan_active', true );					?>					<div id="plan-<?php the_ID(); ?>" class="col-md-3 wow <?php echo get_post_meta( get_the_ID(), 'plan_box_effect', true ); ?>">						<div class="pricing-table <?php if($plan_active == 'active-enabled'): echo 'popular-table d-bg-c'; endif; ?>">							<?php the_title('<h2 class="pricing-table-name">','</h2>'); ?>							<div class="pricing-table-price <?php if($plan_active == 'active-enabled'): echo 'd-text-c'; else: echo 'd-bg-c'; endif; ?>">								<?php echo get_post_meta( get_the_ID(), 'plan_price', true ); ?><!-- <?php _e( '/', 'ulysses' ); ?>								<span>									<?php									if($plan_duration == 'plan-month'):										echo 'per month';									elseif($plan_duration == 'plan-year'):										echo 'per year';									elseif($plan_duration == 'plan-day'):										echo 'per day';									else:										echo '';									endif;									?>-->								</span>							</div>							<ul class="pricing-table-stuff">								<?php								foreach( $plan_features as $key => $value ) :									?>									<li><?php echo esc_attr( $plan_features[$key] ); ?></li>									<?php								endforeach;								?>							</ul>							<p><a href="<?php echo esc_url(get_post_meta( get_the_ID(), 'plan_btn_url', true )); ?>" class="button d-bg-c-h"><?php echo get_post_meta( get_the_ID(), 'plan_btn_txt', true ); ?></a></p>						</div>					</div>					<?php				endwhile;				/* Restore original Post Data */				wp_reset_postdata();				?>			</div>		</div>	</div>	<?php	return ob_get_clean();}add_shortcode('ulysses_pricetable', 'ulysses_pricetable');?>