<?php 
function ulysses_info( $atts ){	global $ulysses_option;
    extract(shortcode_atts(array(		'section_id' => ''    ), $atts));
	if( '' === $section_id ) :
		$section_id = __('shortinfo','ulysses');
	endif;
	// START INFO SECTION 	ob_start();	?>	<div class="info-section" id="<?php echo esc_attr( $section_id ); ?>">		<div class="container">			<div class="col-md-4">				<div class="info-details d-bg-c">					<h4><?php echo esc_attr( $ulysses_option['opt_block1_title'] ); ?></h4>					<ul class="ul-time">						<?php						if ( isset( $ulysses_option['opt_working_hours'] ) ) :	 							$working_hours_list = $ulysses_option['opt_working_hours']; 							if( count( $working_hours_list ) > 0 ) :								foreach ( $working_hours_list as $key => $value ) :									echo '<li>'.$working_hours_list[$key].'</li>';								endforeach;							endif;						endif;						?>					</ul>				</div>				<div class="under-button">					<span></span>					<a href="<?php echo esc_url($ulysses_option['opt_block1_btn_url']); ?>" class="d-border-c d-bg-c-h d-text-c"><?php echo esc_attr( $ulysses_option['opt_block1_btn_txt'] ); ?></a>				</div>			</div>			<div class="col-md-4">				<div class="info-details info-details-center d-bg-c">					<h4><?php echo esc_attr( $ulysses_option['opt_block2_title'] ); ?></h4>					<div class="info-image"><img src="<?php echo esc_url( $ulysses_option['opt_block2_img']['url'] ); ?>" alt="image" /></div>					<ul class="ul-calendar">						<li>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls1_title'] ); ?></span>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls1_time'] ); ?></span>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls1_person'] ); ?></span>						</li>						<li>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls2_title'] ); ?></span>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls2_time'] ); ?></span>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls2_person'] ); ?></span>						</li>						<li>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls3_title'] ); ?></span>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls3_time'] ); ?></span>							<span><?php echo esc_attr( $ulysses_option['opt_block2_cls3_person'] ); ?></span>						</li>					</ul>				</div>				<div class="under-button">					<span></span>					<a href="<?php echo esc_url($ulysses_option['opt_block2_btn_url']); ?>" class="d-border-c d-bg-c-h d-text-c"><?php echo esc_attr( $ulysses_option['opt_block2_btn_txt'] ); ?></a>				</div>			</div>			<div class="col-md-4">				<div class="info-details d-bg-c">					<h4><?php echo esc_attr( $ulysses_option['opt_block3_title'] ); ?></h4>					<ul class="ul-contact">						<li class="ul-contact-1">							<?php echo esc_attr( $ulysses_option['opt_block3_address'] ); ?>						</li>						<li class="ul-contact-2">							<span><?php echo esc_attr( $ulysses_option['opt_block3_contact1'] ); ?></span>							<span><?php echo esc_attr( $ulysses_option['opt_block3_contact2'] ); ?></span>						</li>						<li class="ul-contact-3">							<a href="mailto:<?php echo esc_attr( $ulysses_option['opt_block3_email'] ); ?>"><?php echo esc_attr( $ulysses_option['opt_block3_email'] ); ?></a>						</li>						<li class="ul-contact-4"><?php echo esc_attr( $ulysses_option['opt_block3_skype'] ); ?></li>					</ul>				</div>				<div class="under-button">					<span></span>					<a href="<?php echo esc_url($ulysses_option['opt_block3_btn_url']); ?>" class="d-border-c d-bg-c-h d-text-c"><?php echo esc_attr( $ulysses_option['opt_block3_btn_txt'] ); ?></a>				</div>			</div>		</div>	</div>	<?php	return ob_get_clean();}add_shortcode('ulysses_info', 'ulysses_info');?>