<?php function ulysses_testimonial( $atts ){	global $ulysses_option;    extract(shortcode_atts(array(		'section_id' => '',		'title' => '',		'sub_title' => ''    ), $atts));	if( '' === $section_id ) :		$section_id = __('testimonial','ulysses');	endif;	// START TESTIMONIAL SECTION 	ob_start();	?>	<!-- Trainers Section -->	<div class="trainers-section carousel" id="<?php echo esc_attr( $section_id ); ?>">		<div class="container">			<div class="site-title">				<h1><?php echo esc_attr( $title ); ?></h1>				<p><?php echo esc_attr( $sub_title ); ?></p>				<div class="site-dots d-text-c carousel-arrows"><i class="fa fa-times-2"></i><i class="fa fa-times-2"></i></div>			</div>			<!-- Trainers Owl Carousel -->			<div id="testimonial-slider" class="testimonial-slider">				<?php				$testimonial = array('post_type' => 'testimonial' );				query_posts( $testimonial );				while ( have_posts() ) : the_post();					$imageUrl = wp_get_attachment_image_src( get_post_thumbnail_id(), 'full' );					?>					<div class="item">						<div class="trainer">							<img src="<?php echo esc_url( $imageUrl[0] ); ?>" />							<div class="trainer-info">								<h4><?php echo the_title(); ?></h4>								<p><?php echo the_content(); ?></p>							</div>						</div>					</div>					<?php				endwhile;				// Reset Query				wp_reset_query();				?>			</div><!-- Trainers Owl Carousel Over -->					</div>	</div>	<!-- Trainers Section Over -->	<?php	return ob_get_clean();}add_shortcode('ulysses_testimonial', 'ulysses_testimonial');/*REAL TESTIMONIALfunction ulysses_testimonial( $atts ){    extract(shortcode_atts(array(		'section_id' => '',    ), $atts));	if( '' === $section_id ) :		$section_id = __('testimonial','ulysses');	endif;	ob_start();	?>	<div id="<?php echo esc_attr( $section_id ); ?>" class="testimonials-slider-2">		<div class="testimonials-wrapper">			<div id="testimonial-slider" class="flexslider testimonials-slider-2">				<ul c