<?php 
function ulysses_testimonial( $atts ){    extract(shortcode_atts(array(		'section_id' => '',    ), $atts));	if( '' === $section_id ) :		$section_id = __('testimonial','ulysses');	endif;
	ob_start();	?>	<div id="<?php echo esc_attr( $section_id ); ?>" class="testimonials-slider-2">		<div class="testimonials-wrapper">			<div id="testimonial-slider" class="flexslider testimonials-slider-2">				<ul class="slides">					<?php					$testimonial = array('post_type' => 'testimonial' );					query_posts( $testimonial );					while ( have_posts() ) : the_post();						$testimonial_user = get_post_meta(get_the_ID(), 'testimonial_user', true);						$testimonial_user_designation = get_post_meta(get_the_ID(), 'testimonial_user_designation', true);						?>						<li class="testimonial">							<div class="testimonial-cover">								<?php the_post_thumbnail(); ?>							</div>							<div class="testimonial-content">								<h2 class="d-text-c"><?php the_title(); ?></h2>								<?php the_content(); ?>								<h3><span><?php echo esc_attr( $testimonial_user ); ?></span><?php _e( ',', 'ulysses' ); ?><?php echo esc_attr( $testimonial_user_designation ); ?> </h3>							</div>						</li>						<?php					endwhile;					// Reset Query					wp_reset_query();					?>				</ul>			</div> 		</div>	</div>	<?php	return ob_get_clean();}add_shortcode('ulysses_testimonial', 'ulysses_testimonial');
?>