<?php function ulysses_trainers( $atts ){	global $ulysses_option;    extract(shortcode_atts(array(		'section_id' => '',		'title' => '',		'sub_title' => ''    ), $atts));	if( '' === $section_id ) :		$section_id = __('trainers','ulysses');	endif;	// START TRAINERS SECTION 	ob_start();	?>	<!-- Trainers Section -->	<div class="trainers-section carousel" id="<?php echo esc_attr( $section_id ); ?>">		<div class="container">			<div class="site-title">				<h1><?php echo esc_attr( $title ); ?></h1>				<p><?php echo esc_attr( $sub_title ); ?></p>				<div class="site-dots d-text-c carousel-arrows"><i class="fa fa-times-2"></i><i class="fa fa-times-2"></i></div>			</div>			<!-- Trainers Owl Carousel -->			<div id="trainers-slider" class="trainers-slider">				<?php				$classes = array('post_type' => 'trainers' );				query_posts( $classes );				while ( have_posts() ) : the_post();					$trainer_img_id = get_post_meta(get_the_ID(), 'trainer_image', true);					$trainer_img = wp_get_attachment_url( $trainer_img_id );					$trainer_designation = get_post_meta(get_the_ID(), 'trainer_designation', true);					$trainer_description = get_post_meta(get_the_ID(), 'trainer_description', true);					$trainer_fb = get_post_meta(get_the_ID(), 'trainer_fb', true);					$trainer_twitter = get_post_meta(get_the_ID(), 'trainer_twitter', true);					$trainer_instagram = get_post_meta(get_the_ID(), 'trainer_instagram', true);					$trainer_g_plus = get_post_meta(get_the_ID(), 'trainer_g_plus', true);					?>					<div class="item">						<div class="trainer">							<ul class="socials d-bg-c">								<li><a target="_blank" href="<?php echo esc_url($trainer_fb); ?>"><i class="fa fa-facebook"></i></a></li>								<li><a target="_blank" href="<?php echo esc_url($trainer_twitter); ?>"><i class="fa fa-twitter"></i></a></li>								<li><a target="_blank" href="<?php echo esc_url($trainer_instagram); ?>"><i class="fa fa-instagram"></i></a></li>								<li><a target="_blank" href="<?php echo esc_url($trainer_g_plus); ?>"><i class="fa fa-google-plus"></i></a></li>							</ul>							<img src="<?php echo esc_url( $trainer_img ); ?>" alt="<?php echo esc_attr( $trainer_designation ); ?>" />							<div class="trainer-info">								<h4><?php echo the_title(); ?><span><?php echo esc_attr( $trainer_designation ); ?></span></h4>								<p><?php echo esc_attr( $trainer_description ); ?></p>							</div>						</div>					</div>					<?php				endwhile;				// Reset Query				wp_reset_query();				?>			</div><!-- Trainers Owl Carousel Over -->					</div>	</div>	<!-- Trainers Section Over -->	<?php	return ob_get_clean();}add_shortcode('ulysses_trainers', 'ulysses_trainers');?>